mapper.insert:
INSERT INTO mbtimember( memberName
                     , memberid
                     , memberPassword
                     , phoneNumber
                     , memberEmail
                      )
VALUES(  #{memberName}
      , #{memberid}
      , #{memberPassword}
      , #{phoneNumber}
      , #{memberEmail}
      )

mapper.select:
SELECT memberid, memberPassword
FROM mbtimember
WHERE memberid = #{memberid}
AND memberPassword = #{memberPassword}
